template.pattern:
\begin{description}
  \item[${item}] ${cursor}
\end{description}

\begin{enumerate}
  \item ${cursor}
\end{enumerate}

\begin{itemize}
  \item ${cursor}
\end{itemize}

\begin{table}
\begin{tabular}{${columns}}
${cursor}
\end{tabular}
\caption{${tablecaption}}
\label{${tablelabel}}
\end{table}

%%\usepackage{graphics} is needed for \includegraphics
\begin{figure}[htp]
\begin{center}
  \includegraphics[width=${figureWidth}]{${filename}}
  \caption[${labelInTOC}]{${figureCaption}}
  \label{${figureLabel}}
\end{center}
\end{figure}

